<?defined ('_DSITE') or die ('Access denied');if(!$user->getInfo()&&post('action')!='user_login'){	require_once('../www/login.php');	exit;}//определение навигационных переменных и изменение параметров функций, перелинковка ссылок с .htaccess$path=get('path');//если открыта внутренняя страницаif ($path!=''){	//разбиение адреса по разделам	$path=explode('/',$path);	array_pop($path);	//сначала - последний раздел	$path=array_reverse($path);	//выбираем контент нужного раздела	$q='SELECT * FROM `formetoo_main`.`m_content` WHERE `m_content_id`=(';	//находим id нужного раздела	foreach($path as $node)		$q.='SELECT `m_menu_id` FROM `formetoo_main`.`m_menu` WHERE `m_menu_url`=\''.$node.'\' AND `m_menu_parent`=(';	//родительский пункт самого старшего пункта = 0	$q=substr($q,0,-1).'0';	//закрываем скобки	for($i=0;$i<sizeof($path);$i++)		$q.=')';	if($current=$sql->query($q)) {		$current=$current[0];	//если такого пункта меню нет	} else {		require_once('../www/404.php');		exit;	}}//если открыта главная страницаelse{	$q='SELECT * FROM `m_content` WHERE `m_content_id`=1;';	if($current=$sql->query($q))		$current=$current[0];}//результаты поискаif(isset($_GET['search']))	$current['h1']=$current['title']=$current['description']=$current['keywords']='Поиск по сайту: «'.$_GET['search'].'»';?>